import React from "react";

import styled from "styled-components";
import './header.css';
// import Typist from 'react-typist';
//import Modal from '../../assest/Modal';

const Header = () =>  {
  const ContentWarp = styled.section`
  margin: 3vw 16vw;
  display: flex;
`; //ContentWarp 메인 css

const Contentspan = styled.span`
  color: #2c2f8d;
  font-size: 30px;
`; //ContentWarp 메인 css

const Contentspansub = styled.span`
  font-size: 30px;
`; //ContentWarp 메인 css
const Subnotice = styled.span`
  margin-bottom: 40px;
  font-size: 17px;
`; //ContentWarp 메인 css
const Buttonarea = styled.button`
  margin-top: 15px;
  width: 75px;
  height: 35px;
  border: none;
  border-radius: 5px;
  color: #fff;
  background-color: #8386ce;

`; //ContentWarp 메인 css

const Leftdiv = styled.div`
  width: 50%;
  display: flex;
  flex-direction: column;
`; //ContentWarp 메인 css

const Rightdiv = styled.article`
  position: relative;
  width: 100%;
  margin-left: 5%;
`;

const Contentinner = styled.article`
  padding: 20px;
  background-color: #fff;
  border-radius: 10px;
  width: 100%;
  border: #ededed 1px solid;
  box-shadow: 0 2px 2px 0 rgb(0 0 0 / 19%);
`;

const Titleinput = styled.input`
  width: 240px;
  height: 25px;
  padding: 5px;
  border: 2px solid #6063af;
  border-radius: 5px;
  font-size: 13px;
`;

const Contentinput = styled.textarea`
  width: 365px;
  height: 75px;
  padding: 7px;
  border: 2px solid #6063af;
  border-radius: 5px;
  font-size: 13px;
  margin-top: 15px;
  resize: none;
`;

const Topdiv = styled.div`
  display: flex;
`;

const Tagselect = styled.select`
  width: 65px;
  border: 2px solid #6063af;
  border-radius: 5px;
  margin-left: 5px;
  background: #6063af;
  color: #fff;
  text-align: center;
  -webkit-appearance: none; /* 크롬 화살표 없애기 */
  -moz-appearance: none; /* 파이어폭스 화살표 없애기 */
  appearance: none; /* 화살표 없애기 */
`;

const Tagoption = styled.option`
  background: #fff;
  color: #000;
  text-align: center;
`;

const InnerHeader = styled.header`
  display: flex;
  justify-content: space-between;
  font-size: 14px;
  color: #c4c4c4;
  width: 100%;
`;

const InnerHeaderdiv = styled.div`
  display: flex;
`;

const Innerh2 = styled.h2`
  margin: 0px;
  color: #8386ce;
`;

const Innerptag = styled.p`
  margin: 7px 0 0 15px;
`;

const InnerRightarea = styled.div`
  display: flex;
  width: 15%;
`;

const Tagarea = styled.h2`
  color: #777bd3;
  font-size: 13.5px;
  margin: 0px;
`;

const Reportbutton = styled.a`
  color: #e75858;
  font-size: 13.5px;
  margin-left: 10px;
  text-decoration: none;
`;
const Maintitle = styled.div``;
const Topbr = styled.div`
  border: 1px solid #eee;
  width: 100%;
  margin: 15px 0 15px 0;
`;

const Titleh4 = styled.h3`
  font-size: 18.5px;
  font-weight: 600;
  margin: 10px 0;
`;
const Maincontent = styled.div`
  width: 100%;
  margin: 15px 0;
`;
    return (
      <section className="ContentWarp ">
        <div className="Leftdiv ">
          <span className="Contentspan ">
            {/* <Leftdiv></Leftdiv> */}
            {/* <Typist>
        <b>당신의 이야기를 들려주세요,</b> <br></br>
        </Typist> */}
          </span>
          <span className="Contentspansub">
            무엇이든 괜찮아요.<br></br>
          </span>
          <span className="Subnotice">
            대신 타인에게 상처가 될 수 있는 말은 안되는거 아시죠?<br></br>
          </span>
          <div className="Topdiv">
            <input className="Titleinput"
              type="text"
              placeholder="제목 (최대 20자)"
              maxLength="20"
              onChange={this.handleChange}
              name="bam_title"
            ></input>
            <select className="Tagselect">
              <option className="Tagoption" value="">#태그</option>
              <option className="Tagoption" value="궁금한거" name="bam_teg">
                #궁금한거
              </option>
              <option className="Tagoption" value="학교생활" name="bam_teg">
                #학교생활
              </option>
              <option className="Tagoption" value="취미공유" name="bam_teg">
                #취미공유
              </option>
              <option className="Tagoption" value="일상" name="bam_tag">
                #일상
              </option>
              <option className="Tagoption" value="취업" name="bam_tag">
                #
              </option>
            </select>
          </div>
          <textarea className="Contentinput "
            type="text"
            placeholder="내용 (최대 1000자)&#13;&#10; ※타인에게 모욕감을 줄 수 있는 말은 법적 피해를 받을 수 있습니다."
            maxLength="1000"
            name="bam_content"
          ></textarea>
          <button className="Buttonarea" onClick={this.submitId}>피드작성</button>
          <h1>{this.state.bam_title}</h1>
        </div>
        <article className="Rightdiv ">
          {this.state.bamboocontent
            ? this.state.bamboocontent.map((c) => {
                return (
                  <article className="Contentinner">
                    <header className="InnerHeader">
                      <div className="InnerHeaderdiv">
                        <h2 className="Innerh2">1번째 소식 </h2>
                        <p className="Innerptag">2022-03-28</p>
                      </div>
                      <div className="InnerRightarea">
                        <h2 className="Tagarea">#학교생활</h2>
                        <a className="Reportbutton" href="#">
                          <b>신고</b>
                        </a>
                      </div>
                    </header>
                    <div className="Topbr"></div>
                    <div className="Maintitle">
                      <div className="Titleh4">멀티관 너무 멀어요.</div>
                      <div className="Maincontent">
                        왜 면접은 창조관에서 보면서 수업은 멀티관에서 하죠? 무릎
                        너무 아픕니다.
                      </div>
                    </div>
                  </article>
                );
              })
            : ""}
        </article>
      </section>
    );
  }


export default Header;
