import React, { useState } from 'react'; // import 로 useState 를 불러온다!
import styled from 'styled-components';
const LeftArea = () => {
    const MainSection = styled.section`
        margin: 0px;
        padding:0px;
`; //ContentWarp 메인 css
    const WarpSection = styled.div`
        margin: 0 16vw;
    `;
    const LeftSection = styled.main`
        display: flex;
        justify-content: space-between;
    `;
    const Title = styled.h1`
        margin: 60px 0 27px;
    `;
    const TitleText = styled.p`
        color:#3669c5;
        font-size: 24px;
        font-weight: 700;
        line-height: 27px;
    `;
    const SecondText = styled.p`
        color:#000;
        font-size: 24px;
        font-weight: 700;
        line-height: 0px;
    `;
    const InputArea = styled.div`
        width: 350px;
        margin: 45px 0 0 0;
    `
    const InputTitle = styled.input`
        border-radius: 5px;
        outline: none;
        border: none;
        box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
        padding: 10px;
        font-size: 1em;
        width: 76.5%;
    `
    const TagSelecter = styled.select`
        border-radius: 5px;
        outline: none;
        border: none;
        font-size: 1em;
        padding: 10px 15px;
        text-align: center;
        position: absolute;
        color: #fff;
        background-color: #7ca0e1;
        margin-left: 5px;
        -webkit-appearance:none; /* 크롬 화살표 없애기 */
        -moz-appearance:none; /* 파이어폭스 화살표 없애기 */
        appearance:none; /* 화살표 없애기 */
    `
    const OptionItem = styled.option`
        padding: 7px 0;
        background-color: #fff;
        color: #000;
    `
    const InputConten = styled.div`
        width: calc(100% - -32px);
        margin: 15px 0 0 0 ;
        border-radius: 5px;
        outline: none;
        border: none;
        box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
        padding: 10px 0 10px 10px;

    `
    const InputTextContent = styled.textarea`
        border-radius: 5px;
        outline: none;
        border: none;
        box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
        padding: 10px;
        font-size: 1em;
        width: 94%;
        resize: none;
        box-shadow: none;
        min-height: 80px;
    `
    const ButtonArea = styled.input`
        width: 100px;
        border-radius: 5px;
        outline: none;
        border: none;
        font-size: 1em;
        color: #fff;
        padding: 10px 15px;
        background-color: #7ca0e1;
        margin: 11px 0 0 calc(100% - 57px);
        &:hover {
        background-color: #3866cd;
      }
    
    `
    return(
        <MainSection>
            <WarpSection>
                <LeftSection>
                    <section>
                        <Title>
                            <TitleText>
                                당신의 이야기를 들려주세요,
                            </TitleText>
                            <SecondText>
                                무엇이든 괜찮아요!
                            </SecondText>
                        </Title>
                        <InputArea>
                            <InputTitle type="text" placeholder="제목을 입력하세요." autofocus="" value=""></InputTitle>
                            <TagSelecter>
                                <OptionItem hidden="" disabled="disabled" selected="selected" value="">
                                    태그선택
                                </OptionItem>
                                <OptionItem>
                                    #일상
                                </OptionItem>
                                <OptionItem>
                                    #학교생활
                                </OptionItem>
                                <OptionItem>
                                    #취업
                                </OptionItem>
                                <OptionItem>
                                    #과제
                                </OptionItem>
                                <OptionItem>
                                    #관계
                                </OptionItem>
                            </TagSelecter>
                        </InputArea>
                        <InputConten>
                            <InputTextContent placeholder="이야기를 들려주세요.">
                            </InputTextContent>
                        </InputConten>
                        <ButtonArea type="button" value="피드전송">

                        </ButtonArea>
                    </section>
                </LeftSection>
            </WarpSection>
        </MainSection>
    )
}
export default LeftArea;
