import React, { useState , useRef } from 'react'; // import 로 useState 를 불러온다!
import styled from 'styled-components';
import { render } from "react-dom";
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
} from "react-router-dom";
const MainSection = styled.section`
    margin: 0 16vw;
    padding:0px;

`; //ContentWarp 메인 css
const Ruleh2 = styled.h2`
    margin: 50px 0 27px;
`
const RuleDiv = styled.div`
    color: #3669c5;
    font-size: 24px;
    font-weight: 700;
    line-height: 27px;
`
const Ruledt = styled.dt`
    font-weight: 700;
    font-size: large;
    color:#666;
`
const Rdt = styled.dt`
     font-weight: bold;
    font-size: medium;
    margin: 20px;

`
const Privacy = styled.div`
    float: right;
    margin-bottom: 35px;

`

const TabMenu = styled.ul`
  color: rgba(73, 73, 73, 0.5);
  font-weight: bold;
  display: flex;
  flex-direction: row;
  justify-items: center;
  align-items: center;
  list-style: none;
  margin-top: 3rem;
  margin-bottom: 7rem;
  height: 40px;

  .submenu {
    display: flex;
    justify-content: center;
    flex-grow: 1;
    cursor: pointer;
  }

  .focused {
    color: #666;
    height: 100%;
    display: flex;
    align-items: center;
    transition: 1s;
    border-bottom: #666 1px solid;
  }

  & div.desc {
    text-align: center;
  }
`;

const Desc = styled.div`
  text-align: center;
`;


const Rule = () => {
 //ContentWarp 메인 css

 const menuArr = [
    { name: '이용규칙', content: 'Tab menu ONE' },
    { name: '개인정보처리방침', content: 'Tab menu TWO' },
  ];
  const [currentTab, setCurrentTab] = useState(0)

  const selectMenuHandler = (index) => {
    console.log(index);
    setCurrentTab(index);
  };
    return(
        <MainSection>
            <main>
            <TabMenu>
                {/*TIP: li 엘리먼트의 class명의 경우 선택된 tab 은 'submenu focused' 가 되며, 
                        나머지 2개의 tab은 'submenu' 가 된다.*/}
                {menuArr.map((el, index) => {
                    return <li key={index} 
                            className={`${index === currentTab ? 'submenu focused' : 'submenu'}`}
                            onClick={() => selectMenuHandler(index)}>{el.name}</li>
                })}
            </TabMenu>
                
                <Ruleh2>
                    <RuleDiv>
                        대나무숲 규칙
                    </RuleDiv>
                    <dl>
                        <div>
                            <Ruledt>
                                제 1 장 총 칙
                            </Ruledt>
                            <br></br>
                            <dd>
                                <div>
                                    <Rdt>
                                        제 1 조 (목적 및 효력)
                                    </Rdt>
                                    <Rdt>
                                       (1) 본 규칙은 부산경상대학교 (이하 BSKS라 칭함) 대나무숲 규칙으로, 대나무숲의 투명한 운영과 철저한 익명성 보장을 목적으로 합니다.<br></br>
                                       (2) 이 약관은 웹사이트에서 온라인으로 공시함으로써 효력을 발생하며, 합리적인 사유가 발생할 경우 관련법령에 위배되지 않는 범위 안에서 개정될 수 있습니다.
                                    </Rdt>
                                </div>
                            </dd>
                            <br></br>
                            <dd>
                                <div>
                                    <Rdt>
                                        제 2조 (피드 작성에 대한 규칙)
                                    </Rdt>
                                    <Rdt>
                                        본 규칙은 부산경상대학교 (이하 BSKS라 칭함) 대나무숲 규칙으로, 대나무숲의 투명한 운영과 철저한 익명성 보장을 목적으로 합니다.
                                    </Rdt>
                                    <br></br>
                                    <Rdt>
                                        제 1항
                                    </Rdt>
                                    <Rdt>
                                        다음과 같은 게시물의 경우 반려 처리가 되거나, 삭제가 될 수 있습니다.
                                    </Rdt>
                                    <Rdt>
                                        제 2항
                                    </Rdt>
                                    <Rdt>
                                        제 2조 1항 2호에 해당되는 피드가 만약 공익을 목적으로 하는 고발성 제보일 경우, 관리자 판단 아래 보류나 삭제될 수 있다. 단 다음 기준들이 엄격히 적용됩니다.   
                                    </Rdt>
                                    <Rdt>
                                        제 3항
                                    </Rdt>
                                    <Rdt>
                                    이 외에 제 2조 1항의 기준에 해당되는 제보더라도, 논란의 소지가 없다고 판단되는 범위 내에서 대나무숲 활성화를 위해 대숲지기의 재량으로 게시글이 게시될 수 있습니다. <br></br>단, 다음 기준들이 적용됩니다.   
                                    </Rdt>
                                </div>
                                <br></br>
                                <div>
                                    <Rdt>
                                        제 3조 (게시물의 삭제 또는 이용 제한)
                                    </Rdt>
                                    <Rdt>
                                    회원의 피드의 내용이 다음 각 호에 해당하는 경우 관리자는 해당 게시물을 삭제 또는 해당 회원의 회원 자격을 제한, 정지 또는 상실 시킬 수 있습니다.
                                    </Rdt>
                                    <br></br>
                                    <Rdt>
                                        (1)&nbsp;다른 회원 또는 제3자를 비방하거나 중상 모략으로 명예를 손상시키는 내용<br></br><br></br>
                                        (2)&nbsp;음란물, 욕설 등 공서양속에 위반되는 내용의 정보, 문장, 도형 등을 유포하는 내용<br></br><br></br>
                                        (3)&nbsp;범죄행위와 관련이 있다고 판단되는 내용<br></br><br></br>
