import React, { useState } from 'react'; // import 로 useState 를 불러온다!
import styled from 'styled-components';
import Axios from "axios";

const MainSection = styled.section`
margin: 0px;
padding:0px;
`; //ContentWarp 메인 css
const WarpSection = styled.div`
margin: 0 16vw;
`;
const LeftSection = styled.main`
display: flex;
justify-content: space-between;
`;
const Title = styled.h1`
margin: 60px 0 27px;
`;
const TitleText = styled.p`
color:#3669c5;
font-size: 24px;
font-weight: 700;
line-height: 27px;
`;
const SecondText = styled.p`
color:#000;
font-size: 24px;
font-weight: 700;
line-height: 0px;
`;
const InputArea = styled.div`
width: 350px;
margin: 45px 0 0 0;
`;
const InputTitle = styled.input`

border-radius: 5px;
outline: none;
border: none;
box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
padding: 10px;
font-size: 1em;
width: 76.5%;
`;
const TagSelecter = styled.select`
width: 60px;
border-radius: 5px;
outline: none;
border: none;
font-size: 1em;
padding: 10px 15px;
text-align: center;
position: absolute;
color: #fff;
background-color: #7ca0e1;
margin-left: 5px;
-webkit-appearance:none; /* 크롬 화살표 없애기 */
-moz-appearance:none; /* 파이어폭스 화살표 없애기 */
appearance:none; /* 화살표 없애기 */
`;
const OptionItem = styled.option`
padding: 7px 0;
background-color: #fff;
color: #000;
`;
const InputConten = styled.div`
width: calc(100% - 10px);
margin: 15px 0 0 0 ;
border-radius: 5px;
outline: none;
border: none;
box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
padding: 10px 0 10px 10px;

`;
const InputTextContent = styled.textarea`
border-radius: 5px;
outline: none;
border: none;
box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
padding: 10px;
font-size: 1em;
width: 94%;
resize: none;
box-shadow: none;
min-height: 80px;
`;
const ButtonArea = styled.input`
width: 100px;
border-radius: 5px;
outline: none;
border: none;
font-size: 1em;
color: #fff;
padding: 10px 15px;
background-color: #7ca0e1;
margin: 11px 0 0 calc(100% - 100px);
&:hover {
background-color: #3866cd;
}

`;
const RightArea = styled.article`
position: relative;
width: 100%;
margin-top: 155px;
padding-left: 20px;
margin-bottom: 100px;
`;
const InnerRight = styled.article`
border-radius: 5px;
outline: none;
border: none;
box-shadow: 0 1px 4px rgb(0 0 0 / 25%);
width: calc(68vw - 350px);
padding: 15px;
margin-bottom: 20px;
position: relative;
`;
const InnerHeader = styled.header`
display: flex;
justify-content: space-between;
font-size: 14px;
color: #c4c4c4;
width: 100%;
`;
const InnerDiv1 = styled.div`
display:flex;
`;
const Toph3 = styled.h3`
margin-right: 10px;
font-size: 18px;
color: #3669c5;;
`;
const Timeptag = styled.p`
line-height: 40px;
white-space: pre-wrap;
word-break: break-all;
`;
const SecondDiv = styled.div`
color: #3669c5;
justify-self: flex-end;
display: flex;
`;
const SecondPtag = styled.p`
line-height: 25px;
white-space: pre-wrap;
word-break: break-all;
`;
const ReportBtn = styled.button`
position: relative;
padding: 0 0 0 15px;
font-size: 14px;
background: none;
border: none;
color: #e75858;
margin-top: -15px;
`;
const TitleHeader = styled.h4`
font-size: 16px;
font-weight: 700;
margin: 0;
`;
const Contentp = styled.p`
line-height: 25px;
white-space: pre-wrap;
word-break: break-all;
`;
const ReportA = styled.a`
margin-top: 16px;
text-decoration: none;
`;

const FormSet = styled.form`

`

const LeftArea = () => {
    const [BAM_TAG, setTeg] = useState("");
    const [BAM_TITLE, setTitle] = useState("");
    const [BAM_CONTENT, setContent] = useState("");

    const submitBamboo = () =>{
        Axios.post('http://loaclhost:3001/insert', {
            BAM_TAG:BAM_TAG,
            BAM_TITLE:BAM_TITLE, 
            BAM_CONTENT:BAM_CONTENT 
        }).then(() =>{
            alert("successful insert");
        });
    };



    return(
        <MainSection>
            <WarpSection>
                <LeftSection>
                    <section>
                        <Title>
                            <TitleText>
                                당신의 이야기를 들려주세요,
                            </TitleText>
                            <SecondText>
                                무엇이든 괜찮아요!
                            </SecondText>
                        </Title>
                        <FormSet >
                            <InputArea>
                                <InputTitle type="text" placeholder="제목을 입력하세요." name = "BAM_TITLE" onChange = {(e)=> {
                                    setTitle(e.target.value);
                                }}>

                                </InputTitle>
                                <TagSelecter>
                                    <OptionItem hidden="" disabled="disabled" value="" name="BAM_TAG" onChange = {(e)=> {
                                    setTeg(e.target.value);
                                }}>
                                        태그선택
                                    </OptionItem>
                                    <OptionItem  value="#일상">  
                                        #일상
                                    </OptionItem>
                                    <OptionItem  value="#학교생활">
                                        #학교생활
                                    </OptionItem>
                                    <OptionItem  value="#취업">
                                        #취업
                                    </OptionItem>
                                    <OptionItem value="#과제">
                                        #과제
                                    </OptionItem>
                                    <OptionItem  value="#관계">
                                        #관계
                                    </OptionItem>
                                </TagSelecter>
                            </InputArea>
                            <InputConten>
                                <InputTextContent placeholder="이야기를 들려주세요." name="BAM_CONTENT" value={BAM_CONTENT} onChange = {(e)=> {
                                    setContent(e.target.value);
                                }}>
                                </InputTextContent>
                            </InputConten>
                            <ButtonArea type="button" value="피드전송" onClick={submitBamboo}>
                            </ButtonArea>
                        </FormSet>
                    </section>
                    <RightArea>
                        <InnerRight>
                            <InnerHeader>
                                <InnerDiv1>
                                    <Toph3>
                                        #30번째 이야기
                                    </Toph3>
                                    <Timeptag>
                                        20
                                    </Timeptag>
                                </InnerDiv1>
                                <SecondDiv>
                                    <SecondPtag>
                                        #관계
                                    </SecondPtag>
                                    <ReportA href="/">
                                        <ReportBtn>
                                            신고
                                        </ReportBtn>
                                    </ReportA>

                                </SecondDiv>
                            </InnerHeader>
                            <TitleHeader>
                                제목
                            </TitleHeader>
                            <Contentp>
                                내용
                            </Contentp>
                        </InnerRight>
                    </RightArea>
                </LeftSection>
            </WarpSection>
        </MainSection>
    )
}
export default LeftArea;
